package brainpool_test

import (
	"encoding/base64"
	"testing"

	"github.com/gematik/zero-lab/go/brainpool"
)

var testPFXBase64 = []byte(``)

func TestDecodePKCS12(t *testing.T) {
	testPFX, err := base64.StdEncoding.DecodeString(string(testPFXBase64))
	if err != nil {
		t.Fatalf("base64 decode failed: %v", err)
	}

	_, _, err = brainpool.DecodePKCS12(testPFX, "00")
	if err != nil {
		t.Fatalf("DecodePKCS12 failed: %v", err)
	}
}
